//	        }
//	      },
//	      "commit": {
//	        "type": "commit",
//	        "hash": "eefd5ef5d3df01aed629f650959d6706d54cd335",
//	        "links": {
//	          "self": {
//	            "href": "https://api.bitbucket.org/2.0/repositories/atlassian/bbql/commit/eefd5ef5d3df01aed629f650959d6706d54cd335"
//	          },
//	          "html": {
